mapper.update:
update BILL
   set bill_status = #{bill_status}, publish_date = sysdate
 where pkid = #{bill_pkid}

mapper.select:
select t2.*
  from (select t1.*, rownum row_num
          from (select t.*, count(*) over() row_total
                  from BILL t
                 where t.bill_status <> '0'

                     and t.bid_range = #{bid_range}


                     and t.bill_status = #{bill_status}


                     and t.deliver_address = #{deliver_address}


                     and t.deliver_date = to_date(#{deliver_date}, 'yyyy-mm-dd hh24:mi:ss')


                     and t.pd_type = #{pd_type}

                 order by t.create_date desc) t1) t2
 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select t2.*
  from (select t1.*, rownum row_num
          from (select t.*, count(*) over() row_total
                  from BILL t
                 where t.send_user_pkid = #{send_user_pkid}

                     and t.bill_status = #{bill_status}


                     and t.bill_status = '6'
                     and t.f_send_eval = 'N'

                 order by t.create_date desc) t1) t2
 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select t2.*
  from (select t1.*, rownum row_num
          from (select t.*, count(*) over() row_total
                  from BILL t
                 where t.pd_type = #{pd_type}

                      and t.bill_status = '1'
                      and t.pkid in (select g.bill_pkid
                      from GIVE_PRICE_MSTR g
                      where g.recv_user_pkid = #{recv_user_pkid})


                      and (
                          t.recv_user_pkid = #{recv_user_pkid}
                          and t.bill_status = #{bill_status}
                         )
                       or (
                          t.bill_status = #{bill_status}
                          and t.recv_user_pkid <> #{recv_user_pkid}
                          and t.pkid in (
                                        select g.bill_pkid
                                          from GIVE_PRICE_MSTR g
                                          where g.recv_user_pkid = #{recv_user_pkid}
                                        )
                         )


                      and t.recv_user_pkid = #{recv_user_pkid}
                      and t.bill_status = '6'
                      and t.f_recv_eval = 'N'


                      and (
                      (t.bill_status = '1'
                      and t.pkid in (select g.bill_pkid
                      from GIVE_PRICE_MSTR g
                      where g.recv_user_pkid = #{recv_user_pkid}
                      )
                      )
                      or t.recv_user_pkid = #{recv_user_pkid}
                      or (
                          t.bill_status <> '1'
                          and t.bill_status <> '0'
                          and t.recv_user_pkid <> #{recv_user_pkid}
                          and t.pkid in (
                                  select g.bill_pkid
                                  from GIVE_PRICE_MSTR g
                                  where g.recv_user_pkid = #{recv_user_pkid}
                              )
                          )
                      )

                 order by t.create_date desc) t1) t2
 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1 and #{pageIndex} * #{pageSize}

select count(*)
  from BILL t
 where t.pd_type = #{pd_type}

    and t.bill_status = '1'
    and t.pkid in (select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid})


    and (
        t.recv_user_pkid = #{recv_user_pkid}
        and t.bill_status = #{bill_status}
    )
    or (
        t.bill_status = #{bill_status}
        and t.recv_user_pkid <> #{recv_user_pkid}
        and t.pkid in (
                        select g.bill_pkid
                        from GIVE_PRICE_MSTR g
                        where g.recv_user_pkid = #{recv_user_pkid}
                      )
    )


    and t.recv_user_pkid = #{recv_user_pkid}
    and t.bill_status = '6'
    and t.f_recv_eval = 'N'


    and (
    (t.bill_status = '1'
    and t.pkid in (select g.bill_pkid
    from GIVE_PRICE_MSTR g
    where g.recv_user_pkid = #{recv_user_pkid}
    )
    )
    or t.recv_user_pkid = #{recv_user_pkid}
    or (
        t.bill_status <> '1'
        and t.bill_status <> '0'
        and t.recv_user_pkid <> #{recv_user_pkid}
        and t.pkid in (
                select g.bill_pkid
                from GIVE_PRICE_MSTR g
                where g.recv_user_pkid = #{recv_user_pkid}
        )
    )
    )

select count(*)
  from BILL t
 where t.send_user_pkid = #{send_user_pkid}

     and t.bill_status = #{bill_status}

select t.*
  from GIVE_PRICE_MSTR t
 where t.bill_pkid = #{bill_pkid}
   and t.recv_user_pkid = #{recv_user_pkid}

select t2.* from(select t1.*,rownum row_num from(
        select t.*, count(*) over() row_total
          from RECV_EVAL t
         where t.send_user_pkid = #{send_user_pkid}
         order by t.create_date desc
         ) t1) t2
     where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1
     and #{pageIndex} * #{pageSize}

        select t2.* from(select t1.*,rownum row_num from(
		select t.*, count(*) over() row_total
          from SEND_EVAL t
         where t.send_user_pkid = #{send_user_pkid}
         order by t.create_date desc
		 ) t1) t2
	 where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1
	 and #{pageIndex} * #{pageSize}

select t2.* from(select t1.*,rownum row_num from(
        select t.*, count(*) over() row_total
          from SEND_EVAL t
         where t.recv_user_pkid = #{recv_user_pkid}
         order by t.create_date desc
         ) t1) t2
     where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1
     and #{pageIndex} * #{pageSize}

select t2.* from(select t1.*,rownum row_num from(
        select t.*, count(*) over() row_total
          from RECV_EVAL t
         where t.recv_user_pkid = #{recv_user_pkid}
         order by t.create_date desc
         ) t1) t2
     where t2.row_num between (#{pageIndex} - 1) * #{pageSize} + 1
     and #{pageIndex} * #{pageSize}
